import { createSlice } from "@reduxjs/toolkit";
import { PayloadAction } from "@reduxjs/toolkit";

type TCashbackState = {
  cashbackTotal: number;
  cashbackPeriod: number;
  isLoading: boolean;
  error: null | any;
};

export const initialState: TCashbackState = {
  cashbackTotal: 0,
  cashbackPeriod: 0,
  isLoading: false,
  error: null,
};

const casbackSlice = createSlice({
  name: "cashback",
  initialState,
  reducers: {
    getTotalCashbackRequest(state) {
      state.isLoading = true;
    },
    getPeriodCashbackRequest(state) {
      state.isLoading = true;
    },
    getTotalCashbackSuccess(state, action: PayloadAction<number>) {
      state.cashbackTotal = action.payload;
      state.isLoading = false;
    },

    getPeriodCashbackSuccess(state, action: PayloadAction<number>) {
      state.cashbackPeriod = action.payload;
      state.isLoading = false;
    },

    getTotalCashbackFailed(state, action: PayloadAction<string>) {
      state.isLoading = false;
      state.error = action.payload;
    },

    getPeriodCashbackFailed(state, action: PayloadAction<string>) {
      state.isLoading = false;
      state.error = action.payload;
    },
  },
});

export const {
    getTotalCashbackRequest,
    getPeriodCashbackRequest,
    getTotalCashbackSuccess,
    getPeriodCashbackSuccess,
    getTotalCashbackFailed,
    getPeriodCashbackFailed
} = casbackSlice.actions;

export default casbackSlice.reducer;
