import React, { useState } from "react";
import AuthorizationStyles from "./Authorization.module.scss";
import { TSubmitHandler } from "../../utils/types";
import VisibilityIcon from "@mui/icons-material/Visibility";
import VisibilityOffIcon from "@mui/icons-material/VisibilityOff";
import IconButton from "@mui/material/IconButton";
import InputAdornment from "@mui/material/InputAdornment";

function Authorization() {
  const handleFormSubmit: TSubmitHandler = (e) => {
    e.preventDefault();
    try {
      //   phoneNumberSchema.parse(phoneNumber);
      //   setError("");
      //   navigate("/subscription/confirm");
    } catch (validationError) {
      //   setError("Введите номер телефона");
    }
  };

  const [passwordShown, setPasswordShown] = useState(false);

  const togglePasswordVisiblity = (e: any) => {
    e.preventDefault();
    e.stopPropagation();
    passwordShown ? setPasswordShown(false) : setPasswordShown(true);
  };

  return (
    <div className={AuthorizationStyles.authPageWrapper}>
      <div className={AuthorizationStyles.logoWrapper}>icon</div>
      <div className={AuthorizationStyles.mainContent}>
        <div>
          <h1>Вход</h1>
          <form
            onSubmit={handleFormSubmit}
            className={AuthorizationStyles.formWrapper}
            id="phoneForm"
          >
            <label htmlFor="email" style={{ display: "none" }}>
              Email
            </label>
            <input
              id="email"
              name="email"
              type="text"
              placeholder="Email"
              className={AuthorizationStyles.input}
              required
            />
            <label htmlFor="password" style={{ display: "none" }}>
              Пароль
            </label>
            <div className={AuthorizationStyles.inputField}>
              <input
                id="password"
                name="password"
                type={passwordShown ? "text" : "password"}
                placeholder="Пароль"
                className={AuthorizationStyles.input}
                required
              />
              <div className={AuthorizationStyles.inputAdornment}>
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={togglePasswordVisiblity}
                  edge="end"
                >
                  {passwordShown ? <VisibilityOffIcon /> : <VisibilityIcon />}
                </IconButton>
              </div>
            </div>
            <button type="submit" form="phoneForm" style={{ display: "none" }}>
              Submit
            </button>{" "}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Authorization;
