import React, { useRef, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import PaginationStyles from "./Pagination.module.scss"

type Page = {
    name: string;
    path: string;
  };

  interface PaginationProps {
    pages: Page[];
  }

const Pagination = ({ pages }: PaginationProps) => {

    const navigate = useNavigate();
    const location = useLocation();
    const paginationRef = useRef<HTMLDivElement>(null); // Ref for the container
    const scrollPositionRef = useRef<number>(0); // Ref to store the scroll position

  // Save the current scroll position
  useEffect(() => {
    const handleScroll = () => {
      if (paginationRef.current) {
        scrollPositionRef.current = paginationRef.current.scrollLeft;
      }
    };

    paginationRef.current?.addEventListener('scroll', handleScroll);

    return () => {
      paginationRef.current?.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Apply the saved scroll position
  useEffect(() => {
    if (paginationRef.current) {
      paginationRef.current.scrollLeft = scrollPositionRef.current;
    }
  }, [location.pathname]); // This effect runs when the pathname changes

  const handleItemClick = (path: string) => {
    navigate(path);
  };

  return (
    <div className={PaginationStyles.paginationContainer} ref={paginationRef}>
      {pages.map(page => (
        <div
          key={page.path}
          className={`${PaginationStyles.paginationItem} ${location.pathname === page.path ? PaginationStyles.active : ''}`}
          onClick={() => handleItemClick(page.path)}
        >
          {page.name}
        </div>
      ))}
    </div>
  );
};


export default Pagination;
