import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import PaginationStyles from "./Pagination.module.scss"

type Page = {
    name: string;
    path: string;
  };

  interface PaginationProps {
    pages: Page[];
  }

  const Pagination = ({ pages }: PaginationProps) => {
    const navigate = useNavigate();
    const location = useLocation();

    const handleItemClick = (path: string) => {
      navigate(path);
    };

    // Separate the active page and the other pages
    const activePage = pages.find(page => location.pathname === page.path);
    const nonActivePages = pages.filter(page => location.pathname !== page.path);

    return (
      <div className={PaginationStyles.paginationContainer}>
        {/* Render the active page */}
        {activePage && (
          <div
            key={activePage.path}
            className={`${PaginationStyles.paginationItem} ${PaginationStyles.active}`}
            onClick={() => handleItemClick(activePage.path)}
          >
            {activePage.name}
          </div>
        )}
        {/* Container for non-active pages to push them to the right */}
        <div className={PaginationStyles.nonActiveItems}>
          {nonActivePages.map(page => (
            <div
              key={page.path}
              className={PaginationStyles.paginationItem}
              onClick={() => handleItemClick(page.path)}
            >
              {page.name}
            </div>
          ))}
        </div>
      </div>
    );
  };


export default Pagination;
