import React from 'react';
import { Select, MenuItem, FormControl, InputLabel, ListSubheader, SelectChangeEvent } from '@mui/material';
import PeriodSelectStyles from "./PeriodSelect.module.scss";

const PeriodSelect = () => {
    const [timePeriod, setTimePeriod] = React.useState<string>('Март');
    const [totalPeriod, setTotalPeriod] = React.useState<string>('За месяц');

  const handleChange = (event: SelectChangeEvent) => {
    setTimePeriod(event.target.value);
    // ...
  };

  const handleTotalChange = (event: SelectChangeEvent) => {
    setTotalPeriod(event.target.value);
    // ...
  };

  return (
    <FormControl fullWidth>
      <InputLabel id="time-period-select-label">{timePeriod}</InputLabel>
      <Select
        labelId="time-period-select-label"
        id="time-period-select"
        value={timePeriod}
        label="Month"
        onChange={handleChange}
        style={{ backgroundColor: 'transparent', color: 'white' }} // Customize this to fit your dark theme
      >
        {/* You can use ListSubheader for group caption */}
        <ListSubheader>{timePeriod}</ListSubheader>
        <MenuItem value={'january'}>January</MenuItem>
        <MenuItem value={'february'}>February</MenuItem>
        <MenuItem value={'march'}>March</MenuItem>
        {/* Add other months as necessary */}
      </Select>

      <InputLabel id="total-period-select-label">{totalPeriod}</InputLabel>
      <Select
        labelId="total-period-select-label"
        id="total-period-select"
        value={totalPeriod}
        label="Total"
        onChange={handleTotalChange}
        style={{ backgroundColor: 'transparent', color: 'white' }} // Customize this to fit your dark theme
      >
        {/* You can use ListSubheader for group caption */}
        <ListSubheader>{totalPeriod}</ListSubheader>
        <MenuItem value={'month'}>За месяц</MenuItem>
        <MenuItem value={'total'}>За все время</MenuItem>
        {/* Add other months as necessary */}
      </Select>


    </FormControl>
  );
};

export default PeriodSelect;
