import React, { useRef, useEffect, useLayoutEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import PaginationStyles from "./Pagination.module.scss"

type Page = {
  name: string;
  path: string;
};

interface PaginationProps {
  pages: Page[];
}

const Pagination = ({ pages }: PaginationProps) => {
  const navigate = useNavigate();
  const location = useLocation();
  const paginationRef = useRef<HTMLDivElement>(null);
  const scrollPositionRef = useRef<number>(0);

  useEffect(() => {
    // Restore scroll position when the pathname changes
    if (paginationRef.current) {
      paginationRef.current.scrollLeft = scrollPositionRef.current;
    }
  }, [location.pathname]);

  useLayoutEffect(() => {
    // Add event listener to save scroll position
    const saveScrollPosition = () => {
      if (paginationRef.current) {
        scrollPositionRef.current = paginationRef.current.scrollLeft;
      }
    };
    // Call saveScrollPosition on scroll events
    const paginationCurrent = paginationRef.current;
    paginationCurrent?.addEventListener('scroll', saveScrollPosition);

    return () => {
      paginationCurrent?.removeEventListener('scroll', saveScrollPosition);
    };
  }, []);

  const handleItemClick = (path: string) => {
    // Save current scroll position before navigating
    if (paginationRef.current) {
      scrollPositionRef.current = paginationRef.current.scrollLeft;
    }
    navigate(path);
  };

  return (
    <div ref={paginationRef} className={PaginationStyles.paginationContainer}>
      {pages.map(page => (
        <div
          key={page.path}
          className={`${PaginationStyles.paginationItem} ${location.pathname === page.path ? PaginationStyles.active : ''}`}
          onClick={() => handleItemClick(page.path)}
        >
          {page.name}
        </div>
      ))}
    </div>
  );
};

export default Pagination;
